contracts/facets/AccessFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IAccess} from "../interfaces/IAccess.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract AccessFacet is IAccess {
    function grantRoles(address account, uint256 roleBits) external { GRCStorage.roles().roleBits[account] |= roleBits; emit RoleGranted(account, roleBits); }
    function revokeRoles(address account, uint256 roleBits) external { GRCStorage.roles().roleBits[account] &= ~roleBits; emit RoleRevoked(account, roleBits); }
    function hasRoles(address account, uint256 roleBits) external view returns (bool) { return (GRCStorage.roles().roleBits[account] & roleBits) == roleBits; }
}

contracts/facets/AuditFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IAudit} from "../interfaces/IAudit.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract AuditFacet is IAudit {
    function postPoR(uint64 period, bytes32 merkleRoot, string calldata ipfsRef) external {
        GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period);
        ap.merkleRoot = merkleRoot; ap.ipfs = ipfsRef; emit PoRPosted(period, merkleRoot, ipfsRef); }
    function seal(uint64 period) external { GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period); ap.isSealed = true; emit PoRSealed(period); }
    function getPoR(uint64 period) external view returns (bytes32 merkleRoot, string memory ipfsRef, bool isSealed) { GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period); return (ap.merkleRoot, ap.ipfs, ap.isSealed); }
}

contracts/facets/BondFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IBond} from "../interfaces/IBond.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract BondFacet is IBond {
    function issueLi99(bytes32 series, address to, uint256 notional) external {
        to; GRCStorage.BondSeries storage bs = GRCStorage.bond(series);
        bs.active = true; emit BondIssued(series, to, notional); revert("NOT_IMPL");
    }
    function accrueCoupons(bytes32 series) external returns (uint256 accrued) { series; revert("NOT_IMPL"); }
    function buyback(bytes32 series) external returns (uint256 principalClosed) { series; revert("NOT_IMPL"); }
    function coverageCheck(bytes32 series) external view returns (bool ok, uint256 coverageRatio) { series; return (false,0); }
}

contracts/facets/GovernanceFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IGovernance} from "../interfaces/IGovernance.sol";
import {IDiamondCut} from "../interfaces/IDiamondCut.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract GovernanceFacet is IGovernance {
    function proposeCut(bytes32 id, IDiamondCut.FacetCut[] calldata cut) external { cut; emit CutProposed(id, msg.sender); }
    function queueCut(bytes32 id) external {
        GRCStorage.GovernanceState storage gs = GRCStorage.governance();
        uint256 eta = block.timestamp + gs.timelockSeconds; gs.eta[id] = eta; emit CutQueued(id, eta);
    }
    function executeCut(bytes32 id) external { emit CutExecuted(id); }
    function emergencyBrake(bytes4 selector, bool pause) external { selector; pause; emit EmergencyBrake(selector, pause); }
    function timelock() external view returns (uint256) { return GRCStorage.governance().timelockSeconds; }
    function quorumBps() external view returns (uint256) { return GRCStorage.governance().quorumBps; }
}

contracts/facets/IndexFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IIndex} from "../interfaces/IIndex.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract IndexFacet is IIndex {
    function setWeights(bytes32 indexId, bytes32[] calldata keys, uint256[] calldata weights, uint64 version, bytes32 dashboardHash) external {
        require(keys.length == weights.length, "LEN");
        uint256 sum;
        for(uint i; i<weights.length; i++){ sum += weights[i]; }
        require(sum == 1e18, "SUM");
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        ie.version = version;
        ie.dashboardHash = dashboardHash;
        // overwrite arrays
        delete ie.keys; delete ie.weights;
        for(uint i; i<keys.length; i++){ ie.keys.push(keys[i]); ie.weights.push(weights[i]); }
        emit IndexWeightsUpdated(indexId, version);
    }
    function getIndex(bytes32 indexId) external view returns (uint64 version, bytes32[] memory keys, uint256[] memory weights, bytes32 dashboardHash) {
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        version = ie.version; dashboardHash = ie.dashboardHash; keys = ie.keys; weights = ie.weights;
    }
    function recalcLiCRI(bytes32[] calldata componentIds, uint256[] calldata componentValues) external returns (uint256 faceValueComposite) {
        require(componentIds.length == componentValues.length, "LEN");
        uint256 sum;
        for(uint i; i<componentValues.length; i++){ sum += componentValues[i]; }
        faceValueComposite = sum / componentValues.length; // simple average placeholder
        GRCStorage.IndexState storage isx = GRCStorage.index();
        isx.liCRI = faceValueComposite;
    emit DashboardSnapshot(bytes32(0), isx.globalVersion, faceValueComposite, block.timestamp);
    }
    function setDashboardComposite(bytes32 dashboardHash, uint256 liCRI, uint64 globalVersion) external {
        GRCStorage.IndexState storage isx = GRCStorage.index();
        isx.liCRI = liCRI; isx.globalVersion = globalVersion;
    emit DashboardSnapshot(dashboardHash, globalVersion, liCRI, block.timestamp);
    }
    function getLiCRI() external view returns (uint256) { return GRCStorage.index().liCRI; }
}

contracts/facets/MonetaryFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IMonetary} from "../interfaces/IMonetary.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract MonetaryFacet is IMonetary {
    modifier onlyScalarManager() { /* placeholder for role check */ _; }
    function mintM1(address to, uint256 amount) external { to; amount; revert("NOT_IMPL"); }
    function burnM1(address from, uint256 amount) external { from; amount; revert("NOT_IMPL"); }
    function issueM0(address to, uint256 amount) external { to; amount; revert("NOT_IMPL"); }
    function redeemM0(address from, uint256 amount) external { from; amount; revert("NOT_IMPL"); }
    function setScalarS(uint256 newScalar) external onlyScalarManager {
        GRCStorage.MonetaryState storage ms = GRCStorage.monetary();
        emit ScalarUpdated(ms.scalarS, newScalar);
        ms.scalarS = newScalar;
    }
    function getLayers() external view returns (uint256 m00, uint256 m0, uint256 m1) {
        GRCStorage.MonetaryState storage ms = GRCStorage.monetary();
        return (ms.m00, ms.m0, ms.m1);
    }
}

contracts/facets/PauseFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IPause} from "../interfaces/IPause.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
contract PauseFacet is IPause {
    function setGlobalPause(bool paused) external { GRCStorage.pause().global = paused; emit GlobalPaused(paused); }
    function setFunctionPause(bytes4 selector, bool paused) external { GRCStorage.pause().func[selector] = paused; emit FunctionPaused(selector, paused); }
    function isPaused(bytes4 selector) external view returns (bool) { return GRCStorage.pause().func[selector]; }
    function isGlobalPaused() external view returns (bool) { return GRCStorage.pause().global; }
}

contracts/GrcDiamond.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IDiamondCut} from "./interfaces/IDiamondCut.sol";
import {IDiamondLoupe} from "./interfaces/IDiamondLoupe.sol";
import {IERC165} from "./interfaces/IERC165.sol";
import {IERC173} from "./interfaces/IERC173.sol";
import {IGrcLoupe} from "./interfaces/IGrcLoupe.sol";
import {GRCStorage} from "./libraries/GRCStorage.sol";

contract GrcDiamond is IDiamondCut, IDiamondLoupe, IERC165, IERC173, IGrcLoupe {
    struct SelectorInfo { address facet; }
    mapping(bytes4 => SelectorInfo) internal _selectorInfo;
    address[] private _facetAddresses;              // dynamic list of facet addresses
    mapping(address => bytes4[]) private _facetSelectors; // facet => selectors array
    mapping(address => uint256) private _facetIndex; // facet => index+1 (0 means absent)
    address private _owner;
    string private _grcVersion;

    modifier onlyOwner() { require(msg.sender == _owner, "OWNER"); _; }

    constructor(address owner_, string memory version_) {
        _owner = owner_ == address(0) ? msg.sender : owner_;
        _grcVersion = version_;
        emit OwnershipTransferred(address(0), _owner);
    }

    // IERC173
    function owner() external view override returns (address) { return _owner; }
    function transferOwnership(address newOwner) external override onlyOwner { require(newOwner!=address(0),"ZERO"); emit OwnershipTransferred(_owner,newOwner); _owner=newOwner; }

    // IGrcLoupe minimal wires
    function grcVersion() external view returns (string memory) { return _grcVersion; }
    function getScalarS() external view returns (uint256) { return GRCStorage.monetary().scalarS; }
    function getIndexWeights(bytes32 indexId) external view returns (uint64 ver, bytes32[] memory keys, uint256[] memory weights, bytes32 dashboardHash) {
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        ver = ie.version; keys = ie.keys; weights = ie.weights; dashboardHash = ie.dashboardHash;
    }
    function getPoR(uint64 period) external view returns (bytes32 merkleRoot, string memory ipfs) {
        GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period); merkleRoot=ap.merkleRoot; ipfs=ap.ipfs; }
    function governanceParams() external view returns (uint256 timelockSeconds, uint256 quorumBps) {
        GRCStorage.GovernanceState storage gs = GRCStorage.governance(); return (gs.timelockSeconds, gs.quorumBps); }

    // ERC165
    function supportsInterface(bytes4 interfaceId) external view override returns (bool) {
        return interfaceId == type(IERC165).interfaceId ||
               interfaceId == type(IDiamondCut).interfaceId ||
               interfaceId == type(IDiamondLoupe).interfaceId ||
               interfaceId == type(IERC173).interfaceId ||
               interfaceId == type(IGrcLoupe).interfaceId;
    }

    // DiamondCut
    function diamondCut(FacetCut[] calldata _cut, address _init, bytes calldata _calldata) external override onlyOwner {
        for(uint i; i<_cut.length; i++) {
            FacetCut memory fc = _cut[i];
            if(fc.action == FacetCutAction.Add || fc.action == FacetCutAction.Replace) {
                require(fc.facetAddress != address(0), "FACET_ZERO");
            }
            for(uint s; s<fc.functionSelectors.length; s++) {
                bytes4 sel = fc.functionSelectors[s];
                if(fc.action == FacetCutAction.Add) {
                    require(_selectorInfo[sel].facet == address(0), "EXISTS");
                    _selectorInfo[sel].facet = fc.facetAddress;
                    _addSelectorToFacet(fc.facetAddress, sel);
                } else if(fc.action == FacetCutAction.Replace) {
                    require(_selectorInfo[sel].facet != address(0), "MISSING");
                    address oldFacet = _selectorInfo[sel].facet;
                    if(oldFacet != fc.facetAddress) {
                        _removeSelectorFromFacet(oldFacet, sel);
                        _selectorInfo[sel].facet = fc.facetAddress;
                        _addSelectorToFacet(fc.facetAddress, sel);
                    }
                } else if(fc.action == FacetCutAction.Remove) {
                    require(_selectorInfo[sel].facet != address(0), "MISSING");
                    address oldFacetRemove = _selectorInfo[sel].facet;
                    _selectorInfo[sel].facet = address(0);
                    _removeSelectorFromFacet(oldFacetRemove, sel);
                }
            }
        }
        emit DiamondCut(_cut, _init, _calldata);
        if(_init != address(0)) {
            (bool ok, bytes memory err) = _init.delegatecall(_calldata);
            require(ok, string(err));
        }
    }

    // Loupe minimal (expand later)
    function facets() external view returns (address[] memory facetAddresses, bytes4[][] memory selectors) {
        uint len = _facetAddresses.length;
        facetAddresses = new address[](len);
        selectors = new bytes4[][](len);
        for(uint i; i<len; i++) {
            address f = _facetAddresses[i];
            facetAddresses[i] = f;
            bytes4[] memory sels = _facetSelectors[f];
            selectors[i] = sels;
        }
    }
    function facetFunctionSelectors(address facet) external view returns (bytes4[] memory) { return _facetSelectors[facet]; }
    function facetAddresses() external view returns (address[] memory) { return _facetAddresses; }
    function facetAddress(bytes4 selector) external view returns (address) { return _selectorInfo[selector].facet; }

    // Fallback delegate to facet
    fallback() external payable {
        address facet = _selectorInfo[msg.sig].facet;
        require(facet != address(0), "NO_FACET");
        assembly {
            calldatacopy(0, 0, calldatasize())
            let result := delegatecall(gas(), facet, 0, calldatasize(), 0, 0)
            returndatacopy(0, 0, returndatasize())
            switch result
            case 0 { revert(0, returndatasize()) }
            default { return(0, returndatasize()) }
        }
    }
    receive() external payable {}

    // INTERNALS
    function _addSelectorToFacet(address facet, bytes4 sel) internal {
        if(_facetIndex[facet] == 0) { // new facet
            _facetAddresses.push(facet);
            _facetIndex[facet] = _facetAddresses.length; // index+1
        }
        bytes4[] storage arr = _facetSelectors[facet];
        for(uint i; i<arr.length; i++) { if(arr[i] == sel) return; }
        arr.push(sel);
    }
    function _removeSelectorFromFacet(address facet, bytes4 sel) internal {
        bytes4[] storage arr = _facetSelectors[facet];
        for(uint i; i<arr.length; i++) {
            if(arr[i] == sel) {
                arr[i] = arr[arr.length-1];
                arr.pop();
                break;
            }
        }
        if(arr.length == 0) { _removeFacetAddress(facet); }
    }
    function _removeFacetAddress(address facet) internal {
        uint idxPlus = _facetIndex[facet];
        if(idxPlus == 0) return;
        uint idx = idxPlus - 1;
        uint lastIdx = _facetAddresses.length - 1;
        if(idx != lastIdx) {
            address last = _facetAddresses[lastIdx];
            _facetAddresses[idx] = last;
            _facetIndex[last] = idx + 1;
        }
        _facetAddresses.pop();
        _facetIndex[facet] = 0;
    }
}

