// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IAudit} from "../interfaces/IAudit.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
import {Errors} from "../libraries/Errors.sol";
import {IERC173} from "../interfaces/IERC173.sol";
contract AuditFacet is IAudit {
    uint256 internal constant ROLE_GOVERNANCE = 1 << 1;
    modifier nonReentrant() {
        GRCStorage.Reentrancy storage rs = GRCStorage.reentrancy();
        if(rs.entered == 1) revert Errors.ErrReentrancy();
        rs.entered = 1; _; rs.entered = 0;
    }
    modifier onlyGov() {
        if(IERC173(address(this)).owner() != msg.sender && (GRCStorage.roles().roleBits[msg.sender] & ROLE_GOVERNANCE) == 0) revert Errors.ErrGovernanceRole();
        _;
    }
    function postPoR(uint64 period, bytes32 merkleRoot, string calldata ipfsRef) external onlyGov nonReentrant {
        GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period);
        if(ap.isSealed) revert Errors.ErrPeriodSealed();
        ap.merkleRoot = merkleRoot; ap.ipfs = ipfsRef; ap.isSealed = false; emit PoRPosted(period, merkleRoot, ipfsRef);
    }
    function seal(uint64 period) external onlyGov nonReentrant {
        GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period);
        if(ap.isSealed) revert Errors.ErrPeriodSealed();
        ap.isSealed = true; emit PoRSealed(period);
    }
    function getPoR(uint64 period) external view returns (bytes32 merkleRoot, string memory ipfsRef, bool isSealed) {
        GRCStorage.AuditPeriod storage ap = GRCStorage.audit(period); return (ap.merkleRoot, ap.ipfs, ap.isSealed);
    }
}

contracts/facets/BondFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IBond} from "../interfaces/IBond.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
import {Errors} from "../libraries/Errors.sol";
import {IERC173} from "../interfaces/IERC173.sol";
contract BondFacet is IBond {
    uint256 internal constant ROLE_GOVERNANCE = 1 << 1;
    modifier nonReentrant() {
        GRCStorage.Reentrancy storage rs = GRCStorage.reentrancy();
        if(rs.entered == 1) revert Errors.ErrReentrancy();
        rs.entered = 1; _; rs.entered = 0;
    }
    modifier onlyGovOrOwner() {
        if(IERC173(address(this)).owner() != msg.sender && (GRCStorage.roles().roleBits[msg.sender] & ROLE_GOVERNANCE) == 0) revert Errors.ErrGovernanceRole();
        _;
    }
    function issueLi99(bytes32 series, address to, uint256 notional) external onlyGovOrOwner nonReentrant {
    if(to == address(0)) revert Errors.ErrZeroAddress(); if(notional == 0) revert Errors.ErrZeroNotional();
        GRCStorage.BondSeries storage bs = GRCStorage.bond(series);
    if(bs.active) revert Errors.ErrSeriesExists();
        bs.active = true; bs.principal = notional; bs.couponBps = 500; // 5% placeholder
        bs.termYears = 5; bs.lastCouponTs = uint64(block.timestamp);
        emit BondIssued(series, to, notional);
    }
    function accrueCoupons(bytes32 series) external nonReentrant returns (uint256 accrued) {
        GRCStorage.BondSeries storage bs = GRCStorage.bond(series);
    if(!bs.active) revert Errors.ErrSeriesInactive();
        uint256 elapsed = block.timestamp - bs.lastCouponTs;
        // simple linear accrual: principal * couponBps * elapsed / (YEAR * 10000)
        uint256 yearly = 365 days;
        accrued = bs.principal * bs.couponBps * elapsed / (yearly * 10000);
        bs.lastCouponTs = uint64(block.timestamp);
        emit CouponsAccrued(series, accrued);
    }
    function buyback(bytes32 series) external nonReentrant returns (uint256 principalClosed) {
        GRCStorage.BondSeries storage bs = GRCStorage.bond(series);
        if(!bs.active) revert Errors.ErrSeriesInactive();
        // economic invariant: coverage must be >= principal * 120% if principal > 0
        if(bs.principal > 0) {
            uint256 coverageRatio = bs.principal == 0 ? 0 : (bs.principal * 125) / 100;
            if(coverageRatio < bs.principal * 120 / 100) revert Errors.ErrCoverageFail();
        }
        principalClosed = bs.principal; bs.principal = 0; bs.active = false;
        emit BondBuyback(series, principalClosed);
    }
    function coverageCheck(bytes32 series) external view returns (bool ok, uint256 coverageRatio) {
        GRCStorage.BondSeries storage bs = GRCStorage.bond(series);
        if(!bs.active) return (false,0);
        // Placeholder: coverage ratio = (principal + accrued placeholder) / principal => assume 125% if active
        coverageRatio = bs.principal == 0 ? 0 : (bs.principal * 125) / 100; // 125%
        ok = coverageRatio >= bs.principal * 120 / 100; // >=120%
    }
}

contracts/facets/IndexFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IIndex} from "../interfaces/IIndex.sol";
import {GRCStorage} from "../libraries/GRCStorage.sol";
import {Errors} from "../libraries/Errors.sol";
contract IndexFacet is IIndex {
    uint256 internal constant ROLE_INDEX = 1 << 2;
    modifier nonReentrant() {
        GRCStorage.Reentrancy storage rs = GRCStorage.reentrancy();
        if(rs.entered == 1) revert Errors.ErrReentrancy();
        rs.entered = 1; _; rs.entered = 0;
    }
    modifier onlyIndexRole() {
        if((GRCStorage.roles().roleBits[msg.sender] & ROLE_INDEX) == 0) revert Errors.ErrIndexRole();
        _;
    }
    function setWeights(bytes32 indexId, bytes32[] calldata keys, uint256[] calldata weights, uint64 version, bytes32 dashboardHash) external onlyIndexRole nonReentrant {
    if(keys.length != weights.length) revert Errors.ErrLengthMismatch();
        uint256 sum;
        for(uint i; i<weights.length; i++){ sum += weights[i]; }
    if(sum != 1e18) revert Errors.ErrSumInvalid();
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        ie.version = version;
        ie.dashboardHash = dashboardHash;
        // overwrite arrays
        delete ie.keys; delete ie.weights;
        for(uint i; i<keys.length; i++){ ie.keys.push(keys[i]); ie.weights.push(weights[i]); }
        emit IndexWeightsUpdated(indexId, version);
    }
    function getIndex(bytes32 indexId) external view returns (uint64 version, bytes32[] memory keys, uint256[] memory weights, bytes32 dashboardHash) {
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        version = ie.version; dashboardHash = ie.dashboardHash; keys = ie.keys; weights = ie.weights;
    }
    function recalcLiCRI(bytes32[] calldata componentIds, uint256[] calldata componentValues) external onlyIndexRole nonReentrant returns (uint256 faceValueComposite) {
    if(componentIds.length != componentValues.length) revert Errors.ErrLengthMismatch();
        uint256 sum;
        for(uint i; i<componentValues.length; i++){ sum += componentValues[i]; }
        faceValueComposite = sum / componentValues.length; // simple average placeholder
        GRCStorage.IndexState storage isx = GRCStorage.index();
        isx.liCRI = faceValueComposite;
    emit DashboardSnapshot(bytes32(0), isx.globalVersion, faceValueComposite, block.timestamp);
    }
    function recalcLiCRIWeighted(bytes32[] calldata componentIds, uint256[] calldata componentValues, uint256[] calldata weights) external onlyIndexRole nonReentrant returns (uint256 liCRI) {
        if(componentIds.length != componentValues.length || componentIds.length != weights.length) revert Errors.ErrLengthMismatch();
        uint256 wsum;
        uint256 acc;
        for(uint i; i<weights.length; i++) { wsum += weights[i]; acc += componentValues[i] * weights[i] / 1e18; }
        if(wsum != 1e18) revert Errors.ErrSumInvalid();
        liCRI = acc; // weighted sum already scaled
        GRCStorage.IndexState storage isx = GRCStorage.index();
        isx.liCRI = liCRI;
        emit DashboardSnapshot(bytes32(0), isx.globalVersion, liCRI, block.timestamp);
    }
    function setDashboardComposite(bytes32 dashboardHash, uint256 liCRI, uint64 globalVersion) external onlyIndexRole nonReentrant {
        GRCStorage.IndexState storage isx = GRCStorage.index();
        isx.liCRI = liCRI; isx.globalVersion = globalVersion;
    emit DashboardSnapshot(dashboardHash, globalVersion, liCRI, block.timestamp);
    }
    function getLiCRI() external view returns (uint256) { return GRCStorage.index().liCRI; }
    function setIndexValue(bytes32 indexId, uint256 value) external onlyIndexRole nonReentrant {
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        if(ie.keys.length == 0){ ie.keys.push(bytes32("VALUE")); ie.weights.push(value); }
        else { ie.weights[0] = value; }
    }
    function getIndexValue(bytes32 indexId) external view returns (uint256) {
        GRCStorage.IndexEntry storage ie = GRCStorage.indexEntry(indexId);
        return ie.weights.length == 0 ? 0 : ie.weights[0];
    }
}

