pragma solidity ^0.8.21;
interface IMonetary {
    event ScalarUpdated(uint256 oldScalar, uint256 newScalar);
    function mintM1(address to, uint256 amount) external;
    function burnM1(address from, uint256 amount) external;
    function issueM0(address to, uint256 amount) external;
    function redeemM0(address from, uint256 amount) external;
    function setScalarS(uint256 newScalar) external;
    function getLayers() external view returns (uint256 m00, uint256 m0, uint256 m1);
}

contracts/interfaces/ITriangulation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
interface ITriangulation {
    event RateSet(bytes32 indexed fromAsset, bytes32 indexed toAsset, uint256 rate); // 1e18 scaled
    event Triangulated(bytes32 indexed fromAsset, bytes32 indexed toAsset, uint256 inputAmount, uint256 outputAmount);
    event Redeemed(bytes32 indexed fromAsset, bytes32 indexed toAsset, uint256 inputAmount, uint256 outputAmount);
    event FeeUpdated(uint256 oldFeeBps, uint256 newFeeBps);
    function setRate(bytes32 fromAsset, bytes32 toAsset, uint256 rate) external;
    function getRate(bytes32 fromAsset, bytes32 toAsset) external view returns (uint256 rate);
    function triangulate(bytes32 fromAsset, bytes32 toAsset, uint256 amount) external returns (uint256 outAmount);
    function redeem(bytes32 fromAsset, bytes32 toAsset, uint256 amount) external returns (uint256 outAmount);
    function setFees(uint256 feeBps) external;
    function feeBps() external view returns (uint256);
}

contracts/libraries/Errors.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;

/// @title Centralized custom errors for GRC-2535 diamond + facets
/// @notice Using custom errors reduces deployment and runtime gas versus revert strings.
library Errors {
    // Ownership / Roles
    error ErrOwner();
    error ErrUpgradeRole();
    error ErrGovernanceRole();
    error ErrIndexRole();
    error ErrMonetaryRole();
    error ErrMissingRole();

    // Diamond / Facets / Selectors
    error ErrFacetZero();
    error ErrSelectorExists();
    error ErrSelectorMissing();
    error ErrNoFacet();

    // Pause
    error ErrPausedGlobal();
    error ErrPausedFunc();

    // Array / Math Validation
    error ErrLengthMismatch();
    error ErrSumInvalid();

    // Governance / Timelock
    error ErrNotReady();
    error ErrNoProposal();
    error ErrProposalExists();
    error ErrProposalUsed();

    // Reentrancy / Execution Safety
    error ErrReentrancy();
    error ErrFacetCodeMissing();

    // Scalar / Fees / Bounds
    error ErrScalarBounds();
    error ErrFeeTooHigh();

    // Audit / PoR
    error ErrPeriodSealed();

    // Triangulation / Rates
    error ErrRateUnset();

    // Bond / Series
    error ErrZeroNotional();
    error ErrSeriesExists();
    error ErrSeriesInactive();
    error ErrCoverageFail();

    // Generic / Misc
    error ErrZeroAddress();
}

contracts/libraries/GRCStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
library GRCStorage {
    bytes32 internal constant S_MONETARY = keccak256("grc.storage.monetary");
    bytes32 internal constant S_INDEX    = keccak256("grc.storage.index");
    bytes32 internal constant S_BOND     = keccak256("grc.storage.bond");
    bytes32 internal constant S_GOV      = keccak256("grc.storage.gov");
    bytes32 internal constant S_AUDIT    = keccak256("grc.storage.audit");
    bytes32 internal constant S_ACCESS   = keccak256("grc.storage.access");
    bytes32 internal constant S_PAUSE    = keccak256("grc.storage.pause");
    bytes32 internal constant S_TRIANG   = keccak256("grc.storage.triang");
    bytes32 internal constant S_REENT    = keccak256("grc.storage.reentrancy");

    struct MonetaryState { uint256 m00; uint256 m0; uint256 m1; uint256 scalarS; }
    struct IndexEntry { uint64 version; bytes32 dashboardHash; bytes32[] keys; uint256[] weights; }
    struct IndexState { uint64 globalVersion; uint256 liCRI; mapping(bytes32 => IndexEntry) indices; }
    struct BondSeries { uint256 principal; uint256 coverage; uint256 couponBps; uint64 termYears; uint64 lastCouponTs; bool active; }
    struct AuditPeriod { bytes32 merkleRoot; string ipfs; bool isSealed; }
    struct Roles { mapping(address => uint256) roleBits; }
    struct PauseState { bool global; mapping(bytes4 => bool) func; }
    struct GovernanceState {
        uint256 timelockSeconds; uint256 quorumBps;
        mapping(bytes32 => uint256) eta;                // proposal id => execution time
        mapping(bytes32 => bytes) encodedCut;           // proposal id => abi.encode(FacetCut[])
        mapping(bytes32 => address) initAddr;           // optional init address
        mapping(bytes32 => bytes) initData;             // optional init calldata
        mapping(bytes32 => address) proposer;           // proposer address
    }
    struct TriangulationState { uint256 feeBps; mapping(bytes32 => mapping(bytes32 => uint256)) rate; }
    struct Reentrancy { uint256 entered; }

    // Accessors
    function monetary() internal pure returns (MonetaryState storage ms) { bytes32 p = S_MONETARY; assembly { ms.slot := p } }
    function index() internal pure returns (IndexState storage isx) { bytes32 p = S_INDEX; assembly { isx.slot := p } }
    function indexEntry(bytes32 id) internal pure returns (IndexEntry storage ie) { bytes32 p = keccak256(abi.encode(id, S_INDEX)); assembly { ie.slot := p } }
    function bond(bytes32 slotKey) internal pure returns (BondSeries storage bs) { bytes32 p = keccak256(abi.encode(slotKey, S_BOND)); assembly { bs.slot := p } }
    function audit(uint64 period) internal pure returns (AuditPeriod storage ap) { bytes32 p = keccak256(abi.encode(period, S_AUDIT)); assembly { ap.slot := p } }
    function roles() internal pure returns (Roles storage rs) { bytes32 p = S_ACCESS; assembly { rs.slot := p } }
    function pause() internal pure returns (PauseState storage ps) { bytes32 p = S_PAUSE; assembly { ps.slot := p } }
    function governance() internal pure returns (GovernanceState storage gs) { bytes32 p = S_GOV; assembly { gs.slot := p } }
    function triangulation() internal pure returns (TriangulationState storage ts) { bytes32 p = S_TRIANG; assembly { ts.slot := p } }
    function reentrancy() internal pure returns (Reentrancy storage rs) { bytes32 p = S_REENT; assembly { rs.slot := p } }
}

contracts/test/ReenterInit.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.21;
import {IDiamondCut} from "../interfaces/IDiamondCut.sol";
contract ReenterInit {
    function init(address diamond) external {
        // Attempt recursive diamondCut (empty) to trigger reentrancy guard
        IDiamondCut.FacetCut[] memory cuts;
        IDiamondCut(diamond).diamondCut(cuts, address(0), "");
    }
}

